import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import SignaturePad from "signature_pad";

@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.scss']
})
export class StudentComponent implements OnInit{
  studentId= '';
  student: any = {};
  signaturePad: SignaturePad | undefined;

  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      this.studentId = params.get('id') ?? '';

        fetch(`http://localhost:3001/students/${this.studentId}`)
        .then(response => response.json())
        .then(data => {
            this.student = data;
        });
    });

    const canvas = document.querySelector("canvas");
    this.signaturePad = new SignaturePad(canvas as HTMLCanvasElement, {
        backgroundColor: 'rgb(0,0,0)',
        penColor: 'rgb(255, 255, 255)'
    });
  }

  sign() {
    console.log("signing...")
  }
}
<div>
    <p>student works!</p>
    <b *ngIf="student">Student: {{ student["FIRSTNAME"] }}</b>

    <canvas width="100px" height="100px"></canvas>
    <button (click)="onClick()">Sign</button>
</div>
